INSERT INTO `notifikasi` (`id`, `kode`, `judul`, `jenis`, `isi`, `server`, `tgl_berikutnya`, `updated_at`, `updated_by`, `frekuensi`, `aksi`, `aktif`) VALUES
(1, 'persetujuan_penggunaan', '<i class=\\\"fa fa-file-text-o text-black\\\"></i> &nbsp;Persetujuan Penggunaan OpenSID', 'persetujuan', '<p><b>Untuk menggunakan OpenSID, anda dan desa anda perlu menyetujui ketentuan berikut:</b>\\n                    <ol>\\n                      <li>Pengguna telah membaca dan menyetujui <a href=\\\"https://www.gnu.org/licenses/gpl-3.0.en.html\\\" target=\\\"_blank\\\">Lisensi GPL V3</a>.</li>\\n                     <li>OpenSID gratis dan disediakan \\\"SEBAGAIMANA ADANYA\\\", di mana segala tanggung jawab termasuk keamanan data desa ada pada pengguna.</li>\\n                       <li>Pengguna paham bahwa setiap ubahan OpenSID juga berlisensi GPL V3 yang tidak dapat dimusnahkan, dan aplikasi ubahan itu juga sumber terbuka yang bebas disebarkan oleh pihak yang menerima.</li>\\n                      <li>Pengguna mengetahui, paham dan menyetujui bahwa OpenSID akan mengirim data penggunaan ke server OpenDesa secara berkala untuk tujuan menyempurnakan OpenSID, dengan pengertian bahwa data yang dikirim sama sekali tidak berisi data identitas penduduk atau data sensitif desa lainnya.</li>\\n                 </ol></p>\\n                 <b>Apakah anda dan desa anda setuju dengan ketentuan di atas?</b>', 'client', '2024-06-13 05:47:08', '2024-03-15 05:47:08', 1, 90, 'notif/update_pengumuman,siteman/logout', 1),
(2, 'tracking_off', '<i class=\\\"fa fa-exclamation-triangle text-red\\\"></i> &nbsp;Peringatan Tracking Off', 'peringatan', '<p>Kami mendeteksi bahwa anda telah mematikan fitur tracking. Bila dimatikan, penggunaan website desa anda tidak akan tercatat di server OpenDesa dan tidak akan menerima informasi penting yang sesekali dikirim OpenDesa.</p>\\n                   <br><b>Hidupkan kembali tracking untuk mendapatkan informasi dari OpenDesa?</b>', 'client', '2020-07-29 20:37:42', '2020-07-30 03:37:03', 1, 90, 'setting/aktifkan_tracking,notif/update_pengumuman', 0);
